let imageFile=document.getElementById("post_imageFile_file");function updateImageFileText(){let e=imageFile.value.split(/(\\|\/)/g).pop();Array.from(document.getElementsByClassName("custom-file-label")).forEach(function(t){t.textContent=e}),document.getElementById("post_message").removeAttribute("required")}function submitDeleteForm(){this.parentElement.submit()}imageFile.onchange=updateImageFileText,Array.from(document.getElementsByClassName("delete_button")).forEach(function(e){e.onclick=submitDeleteForm});